+            ->addFieldToFilter('shipping_method', ['neq' => ''])
+            ->addFilter('customer_id', $customer->getId())
+            ->addAttributeToSort('created_at', 'desc')
+            ->setPageSize(1);
+
+        return $orderCollection;
