+    /**
+     * @param $color
+     * @return string
+     */
+    public function mappingColor($color)
+    {
+        if ($color == 'orange')
+            return '#F39801';
+        if ($color == 'green')
+            return '#B6CE5E';
+        if ($color == 'black')
+            return '#4D4D4D';
+        if ($color == 'blue')
+            return '#3398CC';
+        if ($color == 'darkblue')
+            return '#004BA0';
+        if ($color == 'pink')
+            return '#E13B91';
+        if ($color == 'red')
+            return '#E10E03';
+        if ($color == 'violet')
+            return '#B962d5';
+
+        return $color;
+    }
