+            <dt><?php echo $block->escapeHtml($block->getCarrierName($code)) ?></dt>
+            <dd>
+                <ul>
+                    <?php $_sole = $_sole && count($_rates) == 1;
+                    foreach ($_rates as $_rate): ?>
