+                            <?php if ($_rate->getErrorMessage()): ?>
+                                <ul class="messages">
+                                    <li class="error-msg">
+                                        <ul>
+                                            <li><?php echo $block->escapeHtml($_rate->getErrorMessage()) ?></li>
