+                                </label>
+                            <?php endif ?>
+                        </li>
+                    <?php endforeach; ?>
+                </ul>
+            </dd>
+        <?php endforeach; ?>
+        <?php echo $block->getChildHtml('available.extra') ?>
+    </dl>
+    <div class="validation-advice shipping-method" style="display: none;"></div>
