diff --git a/view/frontend/web/js/action/get-payment-information.js b/view/frontend/web/js/action/get-payment-information.js
new file mode 100644
index 0000000..99722d1
--- /dev/null
+++ b/view/frontend/web/js/action/get-payment-information.js
@@ -0,0 +1,54 @@
+/**
+ * Copyright © 2015 Magento. All rights reserved.
+ * See COPYING.txt for license details.
+ */
+define(
+    [
+        'jquery',
+        'Magento_Checkout/js/model/quote',
+        'Magento_Checkout/js/model/url-builder',
+        'mage/storage',
+        'Magento_Checkout/js/model/error-processor',
+        'Magento_Customer/js/model/customer',
+        'Magento_Checkout/js/model/payment/method-converter',
+        'Magento_Checkout/js/model/payment-service',
+        'Mageplaza_Osc/js/model/payment/loader',
+        "mage/url"
+    ],
+    function ($, quote, urlBuilder, storage, errorProcessor, customer, methodConverter, paymentService, paymentLoader, url) {
+        'use strict';
+        return function (deferred, messageContainer) {
+            var serviceUrl;
+
+            deferred = deferred || $.Deferred();
+            /**
+             * Checkout for guest and registered customer.
+             */
+            if (!customer.isLoggedIn()) {
+                serviceUrl = urlBuilder.createUrl('/guest-carts/:cartId/payment-information', {
+                    cartId: quote.getQuoteId()
+                });
+            } else {
+                serviceUrl = urlBuilder.createUrl('/carts/mine/payment-information', {});
+            }
+            url.setBaseUrl(window.secureCheckoutConfig.baseUrl);
+            serviceUrl = url.build(serviceUrl);
+            return storage.get(
+                serviceUrl, false
+            )
+                .done(
+                function (response) {
+                    quote.setTotals(response.totals);
+                    paymentService.setPaymentMethods(methodConverter(response.payment_methods));
+                    deferred.resolve();
+                }
+            ).fail(
+                function (response) {
+                    errorProcessor.process(response, messageContainer);
+                    deferred.reject();
+
+                }
+            );
+        };
+    }
+);
