diff --git a/view/frontend/web/js/action/place-order.js b/view/frontend/web/js/action/place-order.js
new file mode 100644
index 0000000..8156df6
--- /dev/null
+++ b/view/frontend/web/js/action/place-order.js
@@ -0,0 +1,67 @@
+/**
+ * Copyright © 2015 Magento. All rights reserved.
+ * See COPYING.txt for license details.
+ */
+define(
+    [
+        'Magento_Checkout/js/model/quote',
+        'Magento_Checkout/js/model/url-builder',
+        'mage/storage',
+        'mage/url',
+        'Magento_Checkout/js/model/error-processor',
+        'Magento_Customer/js/model/customer',
+        'Mageplaza_Osc/js/model/full-screen-loader'
+    ],
+    function (quote, urlBuilder, storage, url, errorProcessor, customer, fullScreenLoader) {
+        'use strict';
+
+        return function (paymentData, redirectOnSuccess, messageContainer) {
+            var serviceUrl,
+                payload;
+
+            redirectOnSuccess = redirectOnSuccess !== false;
+
+            /** Checkout for guest and registered customer. */
+            if (!customer.isLoggedIn()) {
+                serviceUrl = urlBuilder.createUrl('/guest-carts/:quoteId/payment-information', {
+                    quoteId: quote.getQuoteId()
+                });
+                payload = {
+                    cartId: quote.getQuoteId(),
+                    email: quote.guestEmail,
+                    paymentMethod: paymentData,
+                    billingAddress: quote.billingAddress()
+                };
+            } else {
+                serviceUrl = urlBuilder.createUrl('/carts/mine/payment-information', {});
+                payload = {
+                    cartId: quote.getQuoteId(),
+                    paymentMethod: paymentData,
+                    billingAddress: quote.billingAddress()
+                };
+            }
+
+            fullScreenLoader.startLoader();
+
+            return storage.post(
+                serviceUrl, JSON.stringify(payload)
+            ).done(
+                function (response) {
+                    if (redirectOnSuccess) {
+                        if(response){
+                            window.location.replace(url.build('checkout/onepage/success/'));
+                        }
+                        else{
+                            window.location.replace(url.build('checkout/cart/'));
+                        }
+                    }
+                }
+            ).fail(
+                function (response) {
+                    errorProcessor.process(response, messageContainer);
+                    fullScreenLoader.stopLoader();
+                }
+            );
+        };
+    }
+);
