+                this.saveAddressData(el);
+            }.bind(this));
+            this.applyBilling();
+            if (!customer.isLoggedIn()) {
+                quote.guestEmail = this.billingAddress.emailAddress.getValue();
+            }
+            if (this.billingAddress.container) {
+                billing = Form.serialize(this.billingAddress.container, true);
+
+            }
+            if (this.shippingAddress.container) {
+                shipping = Form.serialize(this.shippingAddress.container, true);
+            }
+            if (billing) {
+                params = Object.extend(params, billing);
+            }
+            if (shipping) {
+                params = Object.extend(params, shipping);
+            }
+            var requestOptions = {
+                method: 'post',
+                parameters: params
+            };
+            MageplazaOsc.Request(this.saveAddressUrlShipping, requestOptions)
+
+        },
+        showShippingAddress: function () {
+            var shippingContainer = this.shippingAddress.container;
+            shippingContainer.setStyle({
+                display: ''
+            });
+            var newHeight = MageplazaOsc.getHeightFromElement(shippingContainer);
+            MageplazaOsc.applyMorphEffect(shippingContainer, newHeight, 0.3, function () {
+                shippingContainer.setStyle({
+                    height: ''
+                })
+                MageplazaOsc.updateNumbers();
+            });
+
+        },
+        hideShippingAddress: function () {
+            var shippingContainer = this.shippingAddress.container;
+            MageplazaOsc.applyMorphEffect(shippingContainer, 0, 0.5, function () {
+                shippingContainer.setStyle({
+                    display: 'none'
+                })
+                MageplazaOsc.updateNumbers();
+            });
+
+        },
+        countrySelectChanged: function (targetAddress) {
+            var regionData = targetAddress.countryRegionData[targetAddress.countrySelect.getValue()];
+            if (regionData) {
+                if (regionData.type === 'regionSelect') {
+                    targetAddress.regionSelect.setValue(regionData.value);
+                }
+                else {
+                    targetAddress.regionInput.setValue(regionData.value);
+                }
+            }
+        },
+        regionSelectChanged: function (targetAddress) {
+            targetAddress.countryRegionData[targetAddress.countrySelect.getValue()] = {
+                'type': 'regionSelect',
+                'value': targetAddress.regionSelect.getValue()
+            }
+        },
+        regionInputChanged: function (targetAddress) {
+            targetAddress.countryRegionData[targetAddress.countrySelect.getValue()] = {
+                'type': 'regionInput',
+                'value': targetAddress.regionInput.getValue()
+            }
+        },
+        triggerElementChanged: function (targetAddress) {
+            var self = this;
+            var flag = targetAddress.triggerElements.all(function (elementId) {
+                var element = $(elementId);
+                if (element) {
+                    return self.validateElement(element);
+                }
+            }, this);
+            if (flag) {
+                this.changeAddressFinish();
+            }
+        },
+        validateElement: function (element) {
+            var className = $w(element.className);
+            var flag = true;
+            className.all(function (name) {
+                var v = Validation.get(name);
+                try {
+                    if (Validation.isVisible(element) && !v.test($F(element), element)) {
+                        flag = false;
+                    } else {
+                        flag = true;
+                    }
+                } catch (e) {
+                    flag = true;
+                }
+            });
+            return flag;
+        },
+        _processAddressSelect: function (targetAddress) {
+            if (targetAddress.addressSelect) {
+                if (targetAddress.addressSelect.value) {
+                    this.hideNewAddressContainer(targetAddress)
+                }
+                else {
+                    this.showNewAddressContainer(targetAddress);
+                }
+            }
+        },
+        showNewAddressContainer: function (targetAddress) {
+            var newAddressContainer = targetAddress.newAddressContainer;
+            newAddressContainer.setStyle({
+                display: ''
+            });
+            var newHeight = MageplazaOsc.getHeightFromElement(newAddressContainer);
+            MageplazaOsc.applyMorphEffect(newAddressContainer, newHeight, 0.3, function () {
+                targetAddress.container.setStyle({
+                    height: ''
+                })
+            }.bind(this));
+        },
+        hideNewAddressContainer: function (targetAddress) {
+            var newAddressContainer = targetAddress.newAddressContainer;
+            MageplazaOsc.applyMorphEffect(newAddressContainer, 0, 0.5, function () {
+                newAddressContainer.setStyle({
+                    display: 'none'
+                })
+            }.bind(this));
+        },
+        saveFormData: function () {
+            MageplazaOsc.saveFormData(this.addressContainer);
+        }
+
+    };
+    return MageplazaOscAddress;
+});
+
+
diff --git a/view/frontend/web/js/osc/address/google-auto-complete.js b/view/frontend/web/js/osc/address/google-auto-complete.js
new file mode 100644
index 0000000..2977431
--- /dev/null
+++ b/view/frontend/web/js/osc/address/google-auto-complete.js
@@ -0,0 +1,171 @@
+/**
+ * Mageplaza
+ *
+ * NOTICE OF LICENSE
+ *
+ * This source file is subject to the Mageplaza.com license that is
+ * available through the world-wide-web at this URL:
+ * https://www.mageplaza.com/LICENSE.txt
+ *
+ * DISCLAIMER
+ *
+ * Do not edit or add to this file if you wish to upgrade this extension to newer
+ * version in the future.
+ *
+ * @category    Mageplaza
+ * @package     Mageplaza_Osc
+ * @copyright   Copyright (c) 2016 Mageplaza (https://www.mageplaza.com/)
+ * @license     https://www.mageplaza.com/LICENSE.txt
+ */
+define(["prototype"], function () {
+    //*Auto detect address by stress Google Map Api*/
+    var MageplazaOscAddressDetect = Class.create();
+    MageplazaOscAddressDetect.prototype = {
+        initialize: function (config) {
+            // init dom elements
+            this.inputSelector = $(config.inputSelector);
+            this.componentForm = config.componentForm;
+            this.addressType = config.addressType;
+            this.addressElementsIds = config.addressElementsIds;
+            this.regionUpdater = config.regionUpdater;
+            this.secureCheckoutAddress = config.secureCheckoutAddress;
+            this.geolocation = $(config.geolocation);
+            this.specificCountry = config.specificCountry;
+            // init observer
+            this.initGoogleAutoComplete();
+        },
+        initGoogleAutoComplete: function () {
+            var me = this;
+            if (this.inputSelector) {
+                var options = {
+                    types: ['geocode']
+                };
+                if (this.specificCountry) {
+                    var restrictions = {
+                        componentRestrictions: {country: this.specificCountry}
+                    };
+                    options = Object.extend(options, restrictions);
+                }
+                //Google Auto Complete
+                this.googleAutoComplete = new google.maps.places.Autocomplete(this.inputSelector, options);
+                google.maps.event.addListener(this.googleAutoComplete, 'place_changed', function () {
+                    me.googleResponse(me.googleAutoComplete.getPlace());
+                });
+            }
+            // Geolocation
+            if (this.geolocation) {
+                this.geolocation.observe('click', function () {
+                    this.geoLocate();
+                }.bind(this));
+            }
+        },
+        googleResponse: function (place) {
+            var street, city, region_id, region, country, postal_code;
+
+            for (var i = 0; i < place.address_components.length; i++) {
+                var addressType = place.address_components[i].types[0];
+                if (this.componentForm[addressType]) {
+                    if (addressType == 'street_number') {
+                        if (street && place.address_components[i][this.componentForm['street_number']])
+                            street += ', ' + place.address_components[i][this.componentForm['street_number']];
+                        else
+                            street = place.address_components[i][this.componentForm['street_number']];
+                    }
+                    if (addressType == 'route') {
+                        if (street && place.address_components[i][this.componentForm['route']])
+                            street += ' ' + place.address_components[i][this.componentForm['route']];
+                        else
+                            street = place.address_components[i][this.componentForm['route']];
+                    }
+                    if (addressType == 'neighborhood') {
+                        if (street && place.address_components[i][this.componentForm['neighborhood']])
+                            street += ', ' + place.address_components[i][this.componentForm['neighborhood']];
+                        else
+                            street = place.address_components[i][this.componentForm['neighborhood']];
+                    }
+                    if (addressType == 'administrative_area_level_2') {
+                        if (street && place.address_components[i][this.componentForm['administrative_area_level_2']])
+                            street += ', ' + place.address_components[i][this.componentForm['administrative_area_level_2']];
+                        else
+                            street = place.address_components[i][this.componentForm['administrative_area_level_2']];
+                    }
+                    if (addressType == 'locality')
+                        city = place.address_components[i][this.componentForm['locality']];
+                    if (addressType == 'administrative_area_level_1') {
+                        region_id = place.address_components[i]['short_name'];
+                        region = place.address_components[i]['long_name'];
+                    }
+                    if (addressType == 'country')
+                        country = place.address_components[i][this.componentForm['country']];
+                    if (addressType == 'postal_code')
+                        postal_code = place.address_components[i][this.componentForm['postal_code']];
+                }
+            }
+            if(place.name){
+                street = place.name;
+            }
+
+            this.responseComponents = {
+                street1: street,
+                city: city,
+                region_id: region,//region_id,
+                region: region,
+                country_id: country,
+                postcode: postal_code,
+            };
+            this.fillAddressById();
+        },
+        fillAddressById: function () {
+            var me = this;
+            var billingAddress = this.secureCheckoutAddress.billingAddress;
+            var shippingAddress = this.secureCheckoutAddress.shippingAddress;
+            this.addressElementsIds.each(function (elementId) {
+                var element = $(me.addressType + ":" + elementId);
+                if (element && me.responseComponents[elementId]) {
+                    element.value = me.responseComponents[elementId];
+                    if(elementId == 'country_id'){
+                        if (me.regionUpdater)
+                            me.regionUpdater.update();
+                    }
+                    if(elementId == 'region_id'){
+                        var option = element.select('[title="' + me.responseComponents[elementId] + '"]');
+                        if(option.length){
+                            option.first().selected = true;
+                        }
+                    }
+                }
+            });
+            //if (this.regionUpdater)
+            //    this.regionUpdater.update();
+            if (this.addressType == 'billing') {
+                this.secureCheckoutAddress.triggerElementChanged(billingAddress);
+            }
+            else {
+                this.secureCheckoutAddress.triggerElementChanged(shippingAddress);
+            }
+        },
+        geoLocate: function () {
+            if (navigator.geolocation) {
+                var me = this;
+                navigator.geolocation.getCurrentPosition(function (position) {
+                    me.getAddressBytLatitude(position.coords.latitude, position.coords.longitude);
+                });
+            }
+        },
+        getAddressBytLatitude: function (myLatitude, myLongitude) {
+            var me = this;
+            var geocoder = new google.maps.Geocoder();
+            var location = new google.maps.LatLng(myLatitude, myLongitude);
+            geocoder.geocode({'latLng': location}, function (results, status) {
+                if (status == google.maps.GeocoderStatus.OK) {
+                    me.googleResponse(results[0]);
+                } else {
+                    return false;
+                }
+            });
+        }
+    };
+    return MageplazaOscAddressDetect;
+});
+
+
diff --git a/view/frontend/web/js/osc/address/region-updater.js b/view/frontend/web/js/osc/address/region-updater.js
new file mode 100644
index 0000000..bf55cac
--- /dev/null
+++ b/view/frontend/web/js/osc/address/region-updater.js
@@ -0,0 +1,213 @@
+/**
+ * Mageplaza
+ *
+ * NOTICE OF LICENSE
+ *
+ * This source file is subject to the Mageplaza.com license that is
+ * available through the world-wide-web at this URL:
+ * https://www.mageplaza.com/LICENSE.txt
+ *
+ * DISCLAIMER
+ *
+ * Do not edit or add to this file if you wish to upgrade this extension to newer
+ * version in the future.
+ *
+ * @category    Mageplaza
+ * @package     Mageplaza_Osc
+ * @copyright   Copyright (c) 2016 Mageplaza (https://www.mageplaza.com/)
+ * @license     https://www.mageplaza.com/LICENSE.txt
+ */
+define(["jquery", "prototype"], function (jq) {
+    RegionUpdater = Class.create();
+    RegionUpdater.prototype = {
+        initialize: function (countryEl, regionTextEl, regionSelectEl, regions, disableAction, clearRegionValueOnDisable) {
+            this.isRegionRequired = true;
+            this.countryEl = $(countryEl);
+            this.regionTextEl = $(regionTextEl);
+            this.regionSelectEl = $(regionSelectEl);
+            this.config = regions['config'];
+            delete regions.config;
+            this.regions = regions;
+            this.disableAction = (typeof disableAction == 'undefined') ? 'hide' : disableAction;
+            this.clearRegionValueOnDisable = (typeof clearRegionValueOnDisable == 'undefined') ? false : clearRegionValueOnDisable;
+
+            if (this.regionSelectEl.options.length <= 1) {
+                this.update();
+            }
+            else {
+                this.lastCountryId = this.countryEl.value;
+            }
+
+            this.countryEl.changeUpdater = this.update.bind(this);
+
+            Event.observe(this.countryEl, 'change', this.update.bind(this));
+        },
+
+        _checkRegionRequired: function () {
+            if (!this.isRegionRequired) {
+                return;
+            }
+
+            var label, wildCard;
+            var elements = [this.regionTextEl, this.regionSelectEl];
+            var that = this;
+            if (typeof this.config == 'undefined') {
+                return;
+            }
+            var regionRequired = this.config.regions_required.indexOf(this.countryEl.value) >= 0;
+
+            elements.each(function (currentElement) {
+                if (!currentElement) {
+                    return;
+                }
+                var form = currentElement.form,
+                    validationInstance = form ? jq(form).data('validation') : null,
+                    field = currentElement.up('.field') || new Element('div');
+
+                if (validationInstance) {
+                    validationInstance.clearError(currentElement);
+                }
+                label = $$('label[for="' + currentElement.id + '"]')[0];
+                if (label) {
+                    wildCard = label.down('em') || label.down('span.required');
+                    var topElement = label.up('tr');// || label.up('li');
+                    if (!that.config.show_all_regions && topElement) {
+                        if (regionRequired) {
+                            topElement.show();
+                        } else {
+                            topElement.hide();
+                        }
+                    }
+                }
+
+                if (label && wildCard) {
+                    if (!regionRequired) {
+                        wildCard.hide();
+                    } else {
+                        wildCard.show();
+                    }
+                }
+
+                //compute the need for the required fields
+                if (!regionRequired || !currentElement.visible()) {
+                    if (field.hasClassName('required')) {
+                        field.removeClassName('required');
+                    }
+                    if (currentElement.hasClassName('required-entry')) {
+                        currentElement.removeClassName('required-entry');
+                    }
+                    if ('select' == currentElement.tagName.toLowerCase() &&
+                        currentElement.hasClassName('validate-select')
+                    ) {
+                        currentElement.removeClassName('validate-select');
+                    }
+                } else {
+                    if (!field.hasClassName('required')) {
+                        field.addClassName('required');
+                    }
+                    if (!currentElement.hasClassName('required-entry')) {
+                        currentElement.addClassName('required-entry');
+                    }
+                    if ('select' == currentElement.tagName.toLowerCase() && !currentElement.hasClassName('validate-select')
+                    ) {
+                        currentElement.addClassName('validate-select');
+                    }
+                }
+            });
+        },
+
+        disableRegionValidation: function () {
+            this.isRegionRequired = false;
+        },
+
+        update: function () {
+            if (this.regions[this.countryEl.value]) {
+
+                if (this.lastCountryId != this.countryEl.value) {
+                    var i, option, region, def;
+
+                    def = this.regionSelectEl.getAttribute('defaultValue');
+                    if (this.regionTextEl) {
+                        if (!def) {
+                            def = this.regionTextEl.value.toLowerCase();
+                        }
+                        this.regionTextEl.value = '';
+                    }
+
+                    this.regionSelectEl.options.length = 1;
+                    for (regionId in this.regions[this.countryEl.value]) {
+                        region = this.regions[this.countryEl.value][regionId];
+
+                        option = document.createElement('OPTION');
+                        option.value = regionId;
+                        option.text = region.name.stripTags();
+                        option.title = region.name;
+
+                        if (this.regionSelectEl.options.add) {
+                            this.regionSelectEl.options.add(option);
+                        } else {
+                            this.regionSelectEl.appendChild(option);
+                        }
+
+                        if (regionId == def || region.name.toLowerCase() == def || region.code.toLowerCase() == def) {
+                            this.regionSelectEl.value = regionId;
+                        }
+                    }
+                }
+
+                if (this.disableAction == 'hide') {
+                    if (this.regionTextEl) {
+                        this.regionTextEl.style.display = 'none';
+                        this.regionTextEl.style.disabled = true;
+                    }
+                    this.regionSelectEl.style.display = '';
+                    this.regionSelectEl.disabled = false;
+                } else if (this.disableAction == 'disable') {
+                    if (this.regionTextEl) {
+                        this.regionTextEl.disabled = true;
+                    }
+                    this.regionSelectEl.disabled = false;
+                }
+                this.setMarkDisplay(this.regionTextEl, false);
+                this.setMarkDisplay(this.regionSelectEl, true);
+
+                this.lastCountryId = this.countryEl.value;
+            } else {
+                if (this.disableAction == 'hide') {
+                    if (this.regionTextEl) {
+                        this.regionTextEl.style.display = '';
+                        this.regionTextEl.style.disabled = false;
+                    }
+                    this.regionSelectEl.style.display = 'none';
+                    this.regionSelectEl.disabled = true;
+                } else if (this.disableAction == 'disable') {
+                    if (this.regionTextEl) {
+                        this.regionTextEl.disabled = false;
+                    }
+                    this.regionSelectEl.disabled = true;
+                    if (this.clearRegionValueOnDisable) {
+                        this.regionSelectEl.value = '';
+                    }
+                } else if (this.disableAction == 'nullify') {
+                    this.regionSelectEl.options.length = 1;
+                    this.regionSelectEl.value = '';
+                    this.regionSelectEl.selectedIndex = 0;
+                    this.lastCountryId = '';
+                }
+                this.setMarkDisplay(this.regionSelectEl, false);
+                this.setMarkDisplay(this.regionTextEl, true);
+            }
+            this._checkRegionRequired();
+        },
+
+        setMarkDisplay: function (elem, display) {
+            var label = $$('label[for="' + elem.id + '"]')[0];
+            if (label) {
+                display ? label.show() : label.hide();
+            }
+        }
+    };
+    return RegionUpdater;
+});
+
+
diff --git a/view/frontend/web/js/osc/auth.js b/view/frontend/web/js/osc/auth.js
