+        'uiRegistry',
+        'Magento_Checkout/js/model/payment/additional-validators',
+        'Magento_Ui/js/model/messages',
+        'uiLayout',
+        'Mageplaza_Osc/js/model/payment/button-checkout-list'
+    ],
+    function (ko,
+              $,
+              Component,
+              placeOrderAction,
+              selectPaymentMethodAction,
+              quote,
+              customer,
+              paymentService,
+              checkoutData,
+              checkoutDataResolver,
+              registry,
+              additionalValidators,
+              Messages,
+              layout,
+              buttonCheckoutList) {
+        'use strict';
+        return Component.extend({
+            redirectAfterPlaceOrder: true,
+            isVisible: false,
+            /**
+             * After place order callback
+             */
+            afterPlaceOrder: function () {
+                //
+            },
+            isPlaceOrderActionAllowed: ko.observable(quote.billingAddress() != null),
+            /**
+             * Initialize view.
+             *
+             * @returns {Component} Chainable.
+             */
+            initialize: function () {
+                this._super().initChildren();
+                quote.billingAddress.subscribe(function (address) {
+                    this.isPlaceOrderActionAllowed((address !== null));
+                }, this);
+                checkoutDataResolver.resolveBillingAddress();
+
+                var billingAddressCode = 'billingAddress' + this.getCode();
+                registry.async('checkoutProvider')(function (checkoutProvider) {
+                    var defaultAddressData = checkoutProvider.get(billingAddressCode);
+                    if (defaultAddressData === undefined) {
+                        // skip if payment does not have a billing address form
+                        return;
+                    }
+                    var billingAddressData = checkoutData.getBillingAddressFromData();
+                    if (billingAddressData) {
+                        checkoutProvider.set(
+                            billingAddressCode,
+                            $.extend({}, defaultAddressData, billingAddressData)
+                        );
+                    }
+                    checkoutProvider.on(billingAddressCode, function (billingAddressData) {
+                        checkoutData.setBillingAddressFromData(billingAddressData);
+                    }, billingAddressCode);
+                });
+                window.buttonCheckoutList = buttonCheckoutList;
+                buttonCheckoutList.registerButton(this.getCode());
+                return this;
+            },
+
+            /**
+             * Initialize child elements
+             *
+             * @returns {Component} Chainable.
+             */
+            initChildren: function () {
+                this.messageContainer = new Messages();
+                this.createMessagesComponent();
+
+                return this;
+            },
+
+            /**
+             * Create child message renderer component
+             *
+             * @returns {Component} Chainable.
+             */
+            createMessagesComponent: function () {
+
+                var messagesComponent = {
+                    parent: this.name,
+                    name: this.name + '.messages',
+                    displayArea: 'messages',
+                    component: 'Magento_Ui/js/view/messages',
+                    config: {
+                        messageContainer: this.messageContainer
+                    }
+                };
+
+                layout([messagesComponent]);
+
+                return this;
+            },
+
+            /**
+             * Place order.
+             */
+            placeOrder: function (data, event) {
+                var self = this,
+                    placeOrder;
+
+                if (event) {
+                    event.preventDefault();
+                }
+
+                if (this.validate()) {
+                    this.isPlaceOrderActionAllowed(false);
+                    placeOrder = placeOrderAction(this.getData(), this.redirectAfterPlaceOrder, this.messageContainer);
+
+                    $.when(placeOrder).fail(function () {
+                        self.isPlaceOrderActionAllowed(true);
+                    }).done(this.afterPlaceOrder.bind(this));
+                    return true;
+                }
+                return false;
+            },
+
+            selectPaymentMethod: function () {
+                selectPaymentMethodAction(this.getData());
+                checkoutData.setSelectedPaymentMethod(this.item.method);
+                return true;
+            },
+
+            isChecked: ko.computed(function () {
+                return quote.paymentMethod() ? quote.paymentMethod().method : null;
+            }),
+
+            isRadioButtonVisible: ko.computed(function () {
+                return paymentService.getAvailablePaymentMethods().length !== 1;
+            }),
+
+            /**
+             * Get payment method data
+             */
+            getData: function () {
+                return {
+                    "method": this.item.method,
+                    "po_number": null,
+                    "additional_data": null
+                };
+            },
+
+            /**
+             * Get payment method type.
+             */
+            getTitle: function () {
+                return this.item.title;
+            },
+
+            /**
+             * Get payment method code.
+             */
+            getCode: function () {
+                return this.item.method;
+            },
+
+            validate: function () {
+                return true;
+            },
+
+            getBillingAddressFormName: function () {
+                return 'billing-address-form-' + this.item.method;
+            },
+
+            disposeSubscriptions: function () {
+                // dispose all active subscriptions
+                var billingAddressCode = 'billingAddress' + this.getCode();
+                registry.async('checkoutProvider')(function (checkoutProvider) {
+                    checkoutProvider.off(billingAddressCode);
+                });
+            }
+        });
+    }
+);
